DROP TABLE IF EXISTS supplier;
CREATE TABLE supplier(supplierName VARCHAR(40) NOT NULL UNIQUE, supplierCode VARCHAR(40) NOT NULL PRIMARY KEY, contactTel VARCHAR(40) NOT NULL, contactName VARCHAR(40) NOT NULL, address VARCHAR(40) NULL, note VARCHAR(40), createTime TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, createUser VARCHAR(40) NOT NULL, updateTime TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, updateUser VARCHAR(40) NULL, state VARCHAR(40) NOT NULL)DEFAULT charset utf8mb4 COLLATE utf8mb4_general_ci;


DROP TABLE IF EXISTS deviceType;
CREATE TABLE deviceType(deviceTypeName VARCHAR(40) NOT NULL UNIQUE, deviceTypeCode VARCHAR(40) NOT NULL PRIMARY KEY, deviceNote VARCHAR(40) NULL, createTime TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, createUser VARCHAR(40) NOT NULL, updateTime TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, updateUser VARCHAR(40) NULL, state VARCHAR(40) NOT NULL)DEFAULT charset utf8mb4 COLLATE utf8mb4_general_ci;


DROP TABLE IF EXISTS deviceInfo;
CREATE TABLE deviceInfo(deviceSort VARCHAR(40) NOT NULL, deviceBrand VARCHAR(40) NOT NULL, deviceTypeName VARCHAR(40) NOT NULL, deviceModel VARCHAR(40) NULL, deviceNumber VARCHAR(40) NOT NULL PRIMARY KEY, installDate DATETIME NOT NULL, longitude VARCHAR(40) NOT NULL, latitude VARCHAR(40) NOT NULL, installAddress VARCHAR(40) NOT NULL, deviceStatus VARCHAR(40) NOT NULL, createTime TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, createUser VARCHAR(40) NOT NULL, updateTime TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, updateUser VARCHAR(40) NULL, state VARCHAR(40) NOT NULL)DEFAULT charset utf8mb4 COLLATE utf8mb4_general_ci;

