DROP TABLE IF EXISTS alarm;
CREATE TABLE alarm(alarmName VARCHAR(40) NOT NULL UNIQUE, alarmCode VARCHAR(40) NOT NULL PRIMARY KEY, alarmLevel VARCHAR(40) NOT NULL, deviceTypeName VARCHAR(40) NOT NULL, alarmStatus VARCHAR(40) NOT NULL, ruleDescription VARCHAR(40) NOT NULL, createTime TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, createUser VARCHAR(40) NOT NULL, updateTime TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, updateUser VARCHAR(40) NULL, state VARCHAR(40) NOT NULL)DEFAULT charset utf8mb4 COLLATE utf8mb4_general_ci;


DROP TABLE IF EXISTS eventConfig;
CREATE TABLE eventConfig(eventName VARCHAR(40) NOT NULL, notificationDescription VARCHAR(40) NOT NULL, eventLevel VARCHAR(40) NOT NULL, deviceTypeName VARCHAR(40) NOT NULL, alarmName VARCHAR(40) NOT NULL, eventConfigId INT UNSIGNED AUTO_INCREMENT PRIMARY KEY, createTime TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, createUser VARCHAR(40) NOT NULL, updateTime TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, updateUser VARCHAR(40) NULL, state VARCHAR(40) NOT NULL)DEFAULT charset utf8mb4 COLLATE utf8mb4_general_ci;


DROP TABLE IF EXISTS eventInfo;
CREATE TABLE eventInfo(eventInfoId INT UNSIGNED AUTO_INCREMENT PRIMARY KEY, eventName VARCHAR(40) NOT NULL, eventInfoStatus VARCHAR(40) NOT NULL, eventInfoResource VARCHAR(40) NOT NULL, alarmFrequencey INT NOT NULL, eventInfoOccurenceTime DATETIME NOT NULL, eventInfoDescription VARCHAR(40) NOT NULL,eventLevel VARCHAR(40) NOT NULL, deviceNumber VARCHAR(40) NOT NULL, deviceTypeName VARCHAR(40) NOT NULL, deviceStatus VARCHAR(40) NOT NULL, addressDescription VARCHAR(40) NOT NULL, installDate DATETIME NOT NULL, opinion VARCHAR(40) DEFAULT NULL, createTime TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, createUser VARCHAR(40) NOT NULL, updateTime TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, updateUser VARCHAR(40) NULL, state VARCHAR(40) NOT NULL)DEFAULT charset utf8mb4 COLLATE utf8mb4_general_ci;

DROP TABLE IF EXISTS eventLog;
CREATE TABLE eventLog(eventInfoId VARCHAR(40) NOT NULL, date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, processStatus VARCHAR(40) NOT NULL, staff VARCHAR(40) DEFAULT NULL, action VARCHAR(40) NOT NULL)DEFAULT charset utf8mb4 COLLATE utf8mb4_general_ci;

DROP TABLE IF EXISTS user;
create table user(userId VARCHAR(40) NOT NULL PRIMARY KEY, userName VARCHAR(40) NOT NULL, userAccount VARCHAR(40) NOT NULL UNIQUE, userPassword VARCHAR(40) NOT NULL, userEmail VARCHAR(40) NULL, userJobNumber VARCHAR(40) NOT NULL UNIQUE, userPhone VARCHAR(40) NOT NULL)default charset utf8mb4 collate utf8mb4_general_ci;
